<template>
  <div class="home-index-panle">
    <div class="home-logo">
      <div id="logo" class="logo-bg"></div>
    </div>
    <div class="home-search-group">
      <a-auto-complete
        class="search-btn"
        enter-button="Search"
        placeholder="请输入小说或作者名称"
        v-model:value.trim="keywords"
        :options="historyList"
        :filter-option="filterOption"
        @keyup.enter.stop="searchDetails()"
      >
      </a-auto-complete>
      <a-button type="primary" @click="searchDetails()">全网查询</a-button>
    </div>

    <div class="search-collet">
      <a
        v-for="(item, index) in cacheBookList"
        :key="index"
        @mouseover="item.isActive = true"
        @mouseout="item.isActive = false"
        @click="toDetail(item)"
      >
        <span
          v-show="item.isActive"
          class="delete-books"
          @click.stop="deleteBook(item)"
        >
          <DeleteOutlined title="删除" />
        </span>
        <div class="tile-icon">
          <a-image
            class="cover"
            :src="item.imgUrl"
            :preview="false"
            fallback="data:image/png;base64,"
          >
          </a-image>
        </div>
        <div class="tile-title">
          <span class="ellipsis" :title="item.bookTitle">
            {{ item.bookTitle }}</span
          >
        </div>
      </a>
    </div>
  </div>
</template>

<script>
import { DeleteOutlined } from "@ant-design/icons-vue";
import { message } from "ant-design-vue";
export default {
  name: "homeIndex",
  data() {
    return { keywords: "" };
  },
  components: { DeleteOutlined },
  computed: {
    cacheBookList() {
      return this.$store.state.caches.readBooksList;
    },
    historyList() {
      let list = this.$store.state.caches.searchHistoryList;
      let newList = [];

      list.forEach((d) => {
        newList.push({ value: d });
      });
      return newList;
    },
  },
  mounted() {
    this.keywords = this.$route.query.keywords;
  },
  methods: {
    filterOption(input, option) {
      return option.value.toUpperCase().indexOf(input.toUpperCase()) >= 0;
    },
    // 直接阅读缓存的书籍
    toDetail(book) {
      // 当前正在阅读的书籍
      this.$router.push({
        path: "/readBooks",
        query: {
          bookUrl: escape(book.bookUrl || ""),
          readUrl: escape(book.readUrl || ""),
        },
      });
    },
    // 跳转查询详情页面
    searchDetails() {
      if (this.keywords.length == 0) {
        message.warning("请输入查询条件！");
        return;
      }
      this.$router.push({
        path: "/searchResult",
        query: { keywords: this.keywords },
      });
    },
    deleteBook(book) {
      // 删除当前书籍
      this.$store.commit("caches/deleteReadBooks", book);
    },
  },
};
</script>

<style scoped>
@import url("./css/index.css");
</style>
