<script>
import { isMobile } from "@/plugins/utils";
import { getMovieIndex } from "@/api/movieApi";
// 目录
import { getCacheImages } from "@/plugins/utils.js";

import { Swiper, SwiperSlide } from "swiper/vue";
import { Pagination, Navigation, Autoplay } from "swiper";

import { Image, Spin } from "ant-design-vue";

import "swiper/css";
import "swiper/css/navigation";
import "swiper/css/pagination";

export default {
  name: "videoIndex",
  setup() {
    let width =
      window.document.getElementsByTagName("body")[0]?.clientWidth ?? 1400;
    let length = Math.floor((width - 200) / 200);
    return {
      length,
      // swiperOptions: {
      //   modules: [Autoplay, Navigation, Pagination],
      //   "slides-per-view": length,
      //   "slides-per-group": length,
      //   scrollbar: true,
      //   keyboard: { enabled: true },
      //   navigation: true,
      //   pagination: { type: "fraction" },
      //   autoplay: {
      //     delay: 10000,
      //     disableOnInteraction: false,
      //   },
      // },
    };
  },
  data() {
    return {
      noCover: require("@/assets/imgs/noCover.jpeg"),
      refreshLoading: false,
      sourceData: {
        tv: [],
        movie: [],
        variety: [],
      },
    };
  },
  components: {
    Image,
    Spin,
    Swiper,
    SwiperSlide,
  },
  computed: {
    // 是否为移动端
    isMobileClass() {
      return isMobile();
    },
    // 视频
    tv() {
      return this.sourceData.dianshiju?.list ?? [];
    },
    // 电影
    movie() {
      return this.sourceData.dianying?.list ?? [];
    },
    // 综艺
    variety() {
      return this.sourceData.zongyi?.list ?? [];
    },

    swiperOptions() {
      debugger;
      return {
        modules: [Autoplay, Navigation, Pagination],
        "slides-per-view": this.length,
        "slides-per-group": this.length,
        scrollbar: true,
        keyboard: { enabled: true },
        navigation: true,
        pagination: { type: "fraction" },
        autoplay: {
          delay: 10000,
          disableOnInteraction: false,
        },
      };
    },
  },
  created() {
    window.onresize = () => {
      let width =
        window.document.getElementsByTagName("body")[0]?.clientWidth ?? 1400;
      this.length = Math.floor((width - 200) / 200);
      this.$forceUpdate();
    };
  },
  mounted() {
    this.init();
  },
  methods: {
    init() {
      // 重新搜索
      this.sourceData = {};
      const key = "videoIndex@key";
      const cacheResult = sessionStorage.getItem(key);
      if (cacheResult) {
        this.sourceData = JSON.parse(cacheResult);
      } else {
        // 打开遮罩
        this.refreshLoading = true;
        getMovieIndex()
          .then((result) => {
            this.refreshLoading = false;
            this.sourceData = result.data.data || {};
            this.sourceData &&
              sessionStorage.setItem(key, JSON.stringify(this.sourceData));
          })
          .catch(() => {
            this.refreshLoading = false;
          });
      }
    },
    // 获取书籍封面
    getImgUrl(item) {
      return getCacheImages(item.imageUrl);
    },
    // 返回首页
    goDetails(item) {
      this.$router.push({
        path: "/videoPlaying",
        query: {
          url: encodeURI(item.pageUrl),
          name: encodeURI(item.name),
          // tag: encodeURI(item.tag),
        },
      });
    },
  },
};
</script>

