<template>
  <div class="video-index-wrap">
    <div class="qy-mod-header">
      <h2 class="qy-mod-title">
        <span class="qy-mod-text">电视剧</span>
      </h2>
    </div>
    <div class="qy-mod-list">
      <ul class="qy-mod-ul">
        <li class="qy-mod-li" v-for="item in tv" :key="item.name">
          <a class="qy-mod-link" href="javascript:0;" @click="goDetails(item)">
            <a-image
              class="movie-img"
              :preview="false"
              :src="getImgUrl(item)"
              fallback="data:image/png;base64,"
            >
            </a-image>
            <span class="qy-mod-link-name ellipsis"> {{ item.name }}</span>
            <span class="icon-tr">爱奇艺</span>
          </a>
          <span class="qy-mod-link-desc ellipsis"> {{ item.desc }}</span>
        </li>
      </ul>
    </div>
    <div class="qy-mod-header">
      <h2 class="qy-mod-title">
        <span class="qy-mod-text">电影</span>
      </h2>
    </div>
    <div class="qy-mod-list">
      <ul class="qy-mod-ul">
        <li class="qy-mod-li" v-for="item in movie" :key="item.name">
          <a class="qy-mod-link" href="javascript:0;" @click="goDetails(item)">
            <a-image
              class="movie-img"
              :preview="false"
              :src="getImgUrl(item)"
              fallback="data:image/png;base64,"
            >
            </a-image>
            <span class="qy-mod-link-name ellipsis"> {{ item.name }}</span>
            <span class="icon-tr">爱奇艺</span>
          </a>
          <span class="qy-mod-link-desc ellipsis"> {{ item.desc }}</span>
        </li>
      </ul>
    </div>
    <div class="qy-mod-header">
      <h2 class="qy-mod-title">
        <span class="qy-mod-text">综艺</span>
      </h2>
    </div>
    <div class="qy-mod-list">
      <ul class="qy-mod-ul">
        <li class="qy-mod-li" v-for="item in variety" :key="item.name">
          <a class="qy-mod-link" href="javascript:0;" @click="goDetails(item)">
            <a-image
              class="movie-img"
              :preview="false"
              :src="getImgUrl(item)"
              fallback="data:image/png;base64,"
            >
            </a-image>
            <span class="qy-mod-link-name ellipsis"> {{ item.name }}</span>
            <span class="icon-tr">爱奇艺</span>
          </a>
          <span class="qy-mod-link-desc ellipsis"> {{ item.desc }}</span>
        </li>
      </ul>
    </div>
  </div>
</template>

<script>
import { isMobile } from "@/plugins/utils";
import { getMovieIndex } from "@/api/movieApi";
// 目录

export default {
  name: "bookIndex",
  data() {
    return {
      bookLoading: false,
      noCover: require("@/assets/imgs/noCover.jpeg"),
      movieList: {
        tv: [],
        movie: [],
        variety: [],
      },
    };
  },
  computed: {
    // 是否为移动端
    isMobileClass() {
      let isTrue = isMobile();
      return isTrue;
    },
    tv() {
      return this.movieList.tv ?? [];
    },
    movie() {
      return this.movieList.movie ?? [];
    },
    variety() {
      return this.movieList.variety ?? [];
    },
  },
  mounted() {
    this.init();
  },
  methods: {
    init() {
      // 重新搜索
      this.movieList = [];
      const key = "videoIndex@key";
      const cacheResult = sessionStorage.getItem(key);
      if (cacheResult) {
        this.movieList = JSON.parse(cacheResult);
      } else {
        // 打开遮罩
        this.refreshLoading = true;
        getMovieIndex()
          .then((result) => {
            this.refreshLoading = false;
            this.movieList = result.data.data || [];
            this.movieList &&
              sessionStorage.setItem(key, JSON.stringify(this.movieList));
          })
          .catch(() => {
            this.refreshLoading = false;
          });
      }
    },
    // 获取书籍封面
    getImgUrl(item) {
      return this.getImages(item.imgSrc) || this.noCover;
    },
    getImages(_url) {
      if (_url !== undefined) {
        return "https://images.weserv.nl/?url=" + _url;
      }
    },
    // 返回首页
    goDetails(item) {
      this.$router.push({
        path: "/playing",
        query: {
          url: encodeURI(item.url),
          name: encodeURI(item.name),
          tag: encodeURI(item.tag),
        },
      });
    },
  },
};
</script>

